import React from "react";
import { Roulette } from "react-custom-roulette";

const PrizeWheel = () => {
  // Define a static list of usernames
  const usernames = ["User1", "User2", "User3", "User4", "User5"];

  // Define the data for the roulette wheel
  const data = usernames.map((username) => ({
    option: username,
    style: { backgroundColor: "yellow", textColor: "black" }, // Customize as needed
  }));

  return (
    <div>
      <Roulette
        data={data}
        // Other props as needed
      />
    </div>
  );
};

export default PrizeWheel;
